import React, { useEffect, useState } from 'react';
import nightBackground from "./sparkles_night.jpg";
import googleLogo from "./google.png";

const MainMenu = () => {
  const [buttonStyle, setButtonStyle] = useState({
    ...styles.loginButton,
    backgroundColor: '#fff',
  });

  useEffect(() => {
    document.title = 'Marketing Dashboard | Home';
  }, []);

  const handleLoginClick = () => {
    window.location.href = 'https://accounts.google.com/o/oauth2/auth?' +
      'client_id=493552220595-5giqmfbhtfuuqudb08vsgft5mhc0hfir.apps.googleusercontent.com&' +
      'redirect_uri=https://ballistic-half-jumper.glitch.me/callback&' +
      'scope=https://www.googleapis.com/auth/userinfo.email%20https://www.googleapis.com/auth/userinfo.profile%20openid&' +
      'response_type=code&' +
      'access_type=offline';
  };

  const handleMouseOver = () => {
    setButtonStyle({
      ...styles.loginButton,
      backgroundColor: '#d4cfcf',
    });
  };

  const handleMouseOut = () => {
    setButtonStyle({
      ...styles.loginButton,
      backgroundColor: '#fff',
    });
  };

  return (
    <div style={styles.wrapper}>
      <div style={styles.menuBar}>
        <div style={styles.menuItem}>Hello</div>
        <div style={styles.menuItem}>World</div>
        <div style={styles.menuItem}>ok</div>
        <div style={styles.menuItem}>you</div>
      </div>
      <div style={styles.mainContainer}>
        <div style={styles.grayContainer}>
          <h1 style={styles.title}>Marketing Dashboard</h1>
          <p style={styles.subtitle}>Your guide to follow your marketing performances</p>
          <button
            style={buttonStyle}
            onClick={handleLoginClick}
            onMouseOver={handleMouseOver}
            onMouseOut={handleMouseOut}
          >
            <img src={googleLogo} alt="Google Logo" style={styles.googleLogo} />
            Continue with Google
          </button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  wrapper: {
    position: 'relative',
    height: '100vh',
  },
  menuBar: {
    backgroundColor: '#666',
    padding: '10px',
    display: 'flex',
    justifyContent: 'space-between',
  },
  menuItem: {
    color: '#fff',
    fontSize: '16px',
    cursor: 'pointer',
    marginRight: '10px',
  },
  mainContainer: {
    position: 'absolute',
    top: '0',
    left: '0',
    width: '100%',
    height: '100%',
    backgroundImage: `url(${nightBackground})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  grayContainer: {
    backgroundColor: '#333',
    padding: '50px',
    borderRadius: '10px',
    textAlign: 'center',
  },
  title: {
    color: '#fff',
    fontSize: '36px',
    marginBottom: '20px',
  },
  subtitle: {
    color: '#fff',
    fontSize: '18px',
    marginBottom: '30px',
  },
  loginButton: {
    display: 'flex',
    alignItems: 'center',
    padding: '10px 20px',
    fontSize: '16px',
    color: '#black',
    backgroundColor: '#333',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
    marginLeft: '24%',
  },
  googleLogo: {
    marginRight: '10px',
    height: '20px',
  },
};

export default MainMenu;
