import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { CircularProgressbarWithChildren } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import nightBackground from "./sparkles_night.jpg";

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [websiteProgress, setWebsiteProgress] = useState(0);
  const [marketingStatus, setMarketingStatus] = useState(0);
  const [activeMenuItem, setActiveMenuItem] = useState("Dashboard");
  const navigate = useNavigate();

  useEffect(() => {
    const getTokenFromUrl = () => {
      const urlSearchParams = new URLSearchParams(window.location.hash.split("?")[1]);
      return urlSearchParams.get("token");
    };

    const saveTokenToLocalStorage = (token) => {
      localStorage.setItem("accessToken", token);
    };

    const fetchUser = async () => {
      try {
        const token = getTokenFromUrl();

        if (!token) {
          navigate("/");
          return;
        }

        saveTokenToLocalStorage(token);
        console.log("Access Token:", token);

        const response = await axios.get("http://localhost:8081/user");
        setUser(response.data);

        setWebsiteProgress(response.data.websiteProgress || 10);
        setMarketingStatus(response.data.marketingStatus || 90);
      } catch (error) {
        console.error("Error fetching user information:", error);
        navigate("/error");
      }
    };

    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      const menuItems = ["Dashboard", "Analytics", "Advertising Performances", "Sales history", "Customer segmentation", "Competitor analysis", "Customer Feedback", "Settings", "Alerts", "Admin"];

      const menuItemOffsets = menuItems.map(item => {
        const element = document.getElementById(item);
        return { item, offsetTop: element.offsetTop - 70 }; // Adjust the offset as needed
      });

      const activeItem = menuItemOffsets.find(offset => scrollPosition >= offset.offsetTop);

      if (activeItem) {
        setActiveMenuItem(activeItem.item);
      }
    };

    const cleanupScrollEvent = () => {
      window.removeEventListener("scroll", handleScroll);
    };

    fetchUser();
    window.addEventListener("scroll", handleScroll);

    return cleanupScrollEvent;
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("accessToken");
    navigate("/");
  };

  return (
    <div style={styles.container}>
      <div style={styles.menu}>
        <ul style={styles.menuList}>
          {["Dashboard", "Analytics", "Advertising Performances", "Sales history", "Customer segmentation", "Competitor analysis", "Customer Feedback", "Settings", "Alerts", "Admin"].map((menuItem) => (
            <li
              key={menuItem}
              style={{
                ...styles.menuItem,
                backgroundColor: menuItem === activeMenuItem ? "#ddd" : "transparent",
              }}
            >
              {menuItem}
            </li>
          ))}
        </ul>
      </div>

      <div style={styles.content} id="Dashboard">
        <h1>Hello</h1>
        {user && (
          <div style={styles.userContainer}>
            <div style={styles.userInfoContainer}>
              <div style={styles.userImageContainer}>
                <img src={user.picture} alt={user.name} style={styles.userImage} />
              </div>
              <div style={styles.userInfo}>
                <p style={styles.userName}>{user.name}</p>
                <button onClick={handleLogout} style={styles.logoutButton}>
                  Log Out
                </button>
              </div>
            </div>
          </div>
        )}
        <div style={styles.websiteProgression}>
          <CircularProgressbarWithChildren
            value={websiteProgress}
            styles={{
              path: { stroke: "#36e617" },
              trail: { stroke: "#525151" },
              text: { fill: "#fff", fontSize: "20px" },
            }}
          >
            <h2>Website Progression</h2>
            <div style={styles.percentage}>{websiteProgress}%</div>
          </CircularProgressbarWithChildren>
        </div>
        <div style={styles.marketingStatus}>
          <CircularProgressbarWithChildren
            value={marketingStatus}
            styles={{
              path: { stroke: "#36e617" },
              trail: { stroke: "#525151" },
              text: { fill: "#fff", fontSize: "20px" },
            }}
          >
            <h2>Marketing<br></br> Status</h2>
            <div style={styles.percentage}>{marketingStatus}%</div>
          </CircularProgressbarWithChildren>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    height: "100vh",
  },
  menu: {
    width: "10%",
    backgroundColor: "#333",
    padding: "20px",
    color: "#fff",
  },
  menuList: {
    listStyle: "none",
    padding: 0,
  },
  menuItem: {
    marginBottom: "10px",
    cursor: "pointer",
    fontSize: "18px",
    marginTop: "30px",
  },
  content: {
    backgroundImage: `url(${nightBackground})`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    flex: 1,
    overflow: "hidden",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  userContainer: {
    position: "absolute",
    top: "10px",
    right: "10px",
  },
  userInfoContainer: {
    backgroundColor: "white",
    padding: "20px",
    width: "200px",
    borderRadius: "10px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    marginBottom: "20px",
    display: "flex",
    alignItems: "center",
  },
  userImageContainer: {
    marginRight: "10px",
    marginBottom: "24%",
  },
  userImage: {
    width: "30px",
    height: "30px",
    borderRadius: "50%",
  },
  userInfo: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  userName: {
    marginTop: "10%",
  },
  logoutButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    padding: "10px 15px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    transition: "background-color 0.3s",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    fontSize: "16px",
    marginTop: "10px",
  },
  websiteProgression: {
    width: "200px",
    textAlign: "center",
    color: "#fff",
    background: "grey",
    borderRadius: "10px",
    padding: "20px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  },
  marketingStatus: {
    width: "200px",
    marginLeft: "3%",
    textAlign: "center",
    color: "#fff",
    background: "grey",
    borderRadius: "10px",
    padding: "20px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  },
  percentage: {
    color: "#fff",
    fontSize: "24px",
    marginTop: "10px",
  },
};

export default Dashboard;
