import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

// Import the image
import sparklesNightImage from "./sparkles_night.jpg";

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const getTokenFromUrl = () => {
      const urlSearchParams = new URLSearchParams(window.location.hash.split("?")[1]);
      return urlSearchParams.get("token");
    };

    const saveTokenToLocalStorage = (token) => {
      localStorage.setItem("accessToken", token);
    };

    const fetchUser = async () => {
      try {
        const token = getTokenFromUrl();

        if (!token) {
          navigate("/");
          return;
        }

        saveTokenToLocalStorage(token);
        console.log("Access Token:", token);

        const response = await axios.get("http://localhost:8081/user");
        setUser(response.data);
      } catch (error) {
        console.error("Error fetching user information:", error);
        navigate("/error");
      }
    };

    fetchUser();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("accessToken");
    navigate("/");
  };

  return (
    <div style={{ ...styles.dashboardContainer, backgroundImage: `url(${sparklesNightImage})` }}>
      <h1>Hello</h1>
      {/* {user && (
        <div style={styles.userContainer}>
          <div>
            <img src={user.picture} alt={user.name} style={styles.userImage} />
            {user.name}
          </div>
          <button onClick={handleLogout} style={styles.logoutButton}>
            Log Out
          </button>
        </div>
      )} */}
    </div>
  );
};

const styles = {
  dashboardContainer: {
    backgroundImage: `url(${sparklesNightImage})`,
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    minHeight: "100vh",
  },
  userContainer: {
    position: "absolute",
    top: "10px",
    right: "10px",
  },
  userImage: {
    width: "30px",
    height: "30px",
    borderRadius: "50%",
    marginRight: "10px",
  },
  logoutButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    marginTop: "5%",
    marginLeft: "25%",
    padding: "10px 15px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    transition: "background-color 0.3s",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    fontSize: "16px",
  },
};

export default Dashboard;
