import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Function to get the token from the URL
    const getTokenFromUrl = () => {
      const urlSearchParams = new URLSearchParams(window.location.search);
      return urlSearchParams.get("token");
    };

    // Function to save the token to localStorage
    const saveTokenToLocalStorage = (token) => {
      localStorage.setItem("accessToken", token);
    };

    const fetchUser = async () => {
      try {
        // Get the token from the URL
        const token = getTokenFromUrl();

        // Save the token to localStorage
        saveTokenToLocalStorage(token);
        console.log("Access Token:", token);

        // Make a request to your server to fetch the user information
        const response = await axios.get("http://localhost:8081/user"); // Replace with your server endpoint
        setUser(response.data);
      } catch (error) {
        console.error("Error fetching user information:", error);
        // Handle error or redirect to login page
        navigate("/error");
      }
    };

    fetchUser();
  }, [navigate]);

  return (
    <div>
      <h1>Hello</h1>
      {user && (
        <div style={{ position: "absolute", top: "10px", right: "10px" }}>
          <div>
            <img
              src={user.picture}
              alt={user.name}
              style={{
                width: "30px",
                height: "30px",
                borderRadius: "50%",
                marginRight: "10px",
              }}
            />
            {user.name}
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
