import React, { useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import nightBackground from "./sparkles_night.jpg";
import { Chart } from "chart.js/auto";

const Analytics = () => {
  const [user, setUser] = useState(null);
  const [hoveredItem, setHoveredItem] = useState(null);
  const navigate = useNavigate();

  const chartRef = useRef(null);

  const [chartData, setChartData] = useState({
    labels: [],
    datasets: [
      {
        label: "Selling History",
        data: [],
        borderColor: "rgba(75,192,192,1)",
        borderWidth: 2,
        fill: false,
      },
    ],
  });

  useEffect(() => {
    const getTokenFromLocalStorage = () => {
      return localStorage.getItem("accessToken");
    };

    const storedKey = localStorage.getItem("generatedKey");
    console.log("Stored key is: ", storedKey);

    const fetchData = async () => {
      try {
        const response = await axios.get(`https://ballistic-half-jumper.glitch.me/${storedKey}`);
        const data = response.data.data || [];

        const updatedChartData = {
          labels: data.map(item => item.date),
          datasets: [
            {
              label: "Selling History",
              data: data.map(item => item.Sold),
              borderColor: "rgba(75,192,192,1)",
              borderWidth: 2,
              fill: false,
            },
          ],
        };

        setChartData(updatedChartData);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    const fetchUser = async () => {
      try {
        const token = getTokenFromLocalStorage();

        if (!token) {
          navigate("/");
          return;
        }

        const response = await axios.get(
          "https://ballistic-half-jumper.glitch.me/user"
        );
        setUser(response.data);
        document.title = "Marketing Dashboard | Analytics";
      } catch (error) {
        console.error("Error fetching user information:", error);
        navigate("/error");
      }
    };

    fetchUser();
    fetchData();
  }, [navigate]);

  useEffect(() => {
    if (chartRef.current) {
      chartRef.current.destroy();
    }

    const newChartInstance = new Chart("chart", {
      type: "line",
      data: chartData,
      options: styles.chartOptions,
    });

    chartRef.current = newChartInstance;

    return () => {
      if (chartRef.current) {
        chartRef.current.destroy();
      }
    };
  }, [chartData]);

  const handleMenuItemClick = (path) => {
    navigate(path);
  };

  const handleMenuItemHover = (index) => {
    setHoveredItem(index);
  };

  const handleMenuItemLeave = () => {
    setHoveredItem(null);
  };

  const handleLogout = () => {
    localStorage.removeItem("accessToken");
    navigate("/");
  };

  return (
    <div style={styles.container}>
      <div style={styles.menu}>
        <ul style={styles.menuList}>
          {/* ... (Menu items remain unchanged) */}
        </ul>
      </div>

      <div style={styles.content}>
        {user && (
          <div style={styles.userContainer}>
            <div style={styles.userInfoContainer}>
              <div style={styles.userImageContainer}>
                <img
                  src={user.picture}
                  alt={user.name}
                  style={styles.userImage}
                />
              </div>
              <div style={styles.userInfo}>
                <p style={styles.userName}>{user.name}</p>
                <button onClick={handleLogout} style={styles.logoutButton}>
                  Log Out
                </button>
              </div>
            </div>
          </div>
        )}

        <div style={styles.chartBackgroundContainer}>
          <div style={styles.chartContainer}>
            <canvas id="chart" />
          </div>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    height: "100vh",
  },
  menu: {
    width: "10%",
    backgroundColor: "#333",
    padding: "20px",
    color: "#fff",
  },
  menuList: {
    listStyle: "none",
    padding: 0,
  },
  menuItem: {
    marginBottom: "10px",
    cursor: "pointer",
    fontSize: "18px",
    marginTop: "30px",
    transition: "background-color 0.3s",
  },
  content: {
    backgroundImage: `url(${nightBackground})`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    flex: 1,
    overflow: "hidden",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  userContainer: {
    position: "absolute",
    top: "10px",
    right: "10px",
  },
  userInfoContainer: {
    backgroundColor: "white",
    padding: "20px",
    width: "200px",
    borderRadius: "10px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    marginBottom: "20px",
    display: "flex",
    alignItems: "center",
  },
  userImageContainer: {
    marginRight: "10px",
    marginBottom: "24%",
  },
  userImage: {
    width: "30px",
    height: "30px",
    borderRadius: "50%",
  },
  userInfo: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  userName: {
    marginTop: "10%",
  },
  logoutButton: {
    backgroundColor: "#4CAF50",
    color: "white",
    padding: "10px 15px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    transition: "background-color 0.3s",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    fontSize: "16px",
    marginTop: "10px",
  },
  chartBackgroundContainer: {
    flex: 1,
    padding: "2px",
    backgroundColor: "#fff",
    marginLeft: "15%",
    marginRight: "15%",
  },

  chartContainer: {
    flex: 1,
    width: "80%",
    marginLeft: "10%",
  },
  chartOptions: {
    scales: {
      x: [
        {
          type: "category",
          labels: ["Jan", "Feb", "Mar", "Apr", "May", "Jun"],
        },
      ],
      y: [
        {
          type: "linear",
          ticks: {
            beginAtZero: true,
          },
        },
      ],
    },
  },
};

export default Analytics;
