import React, { useEffect, useState } from 'react';
import nightBackground from "./sparkles_night.jpg";

const MainMenu = () => {
  const [buttonStyle, setButtonStyle] = useState({
    ...styles.loginButton,
    backgroundColor: '#3498db', // Initial background color
  });

  const handleLoginClick = () => {
    window.location.href = 'https://accounts.google.com/o/oauth2/auth?' +
      'client_id=493552220595-5giqmfbhtfuuqudb08vsgft5mhc0hfir.apps.googleusercontent.com&' +
      'redirect_uri=https://ballistic-half-jumper.glitch.me/callback&' +
      'scope=https://www.googleapis.com/auth/userinfo.email%20https://www.googleapis.com/auth/userinfo.profile%20openid&' +
      'response_type=code&' +
      'access_type=offline';
  };

  const handleMouseOver = () => {
    setButtonStyle({
      ...styles.loginButton,
      backgroundColor: '#2980b9', // Hover background color
    });
  };

  useEffect(() => {
    document.title = "Marketing Dashboard | Feedback";
  }, []);

  const handleMouseOut = () => {
    setButtonStyle({
      ...styles.loginButton,
      backgroundColor: '#3498db', // Reset to initial background color
    });
  };

  return (
    <div style={styles.mainContainer}>
      <div style={styles.grayContainer}>
        <h1 style={styles.title}>Marketing Dashboard</h1>
        <p style={styles.subtitle}>Your guide to follow your marketing performances</p>
        <button
          style={buttonStyle}
          onClick={handleLoginClick}
          onMouseOver={handleMouseOver}
          onMouseOut={handleMouseOut}
        >
          Continue with Google
        </button>
      </div>
    </div>
  );
};

const styles = {
  mainContainer: {
    height: '100vh',
    backgroundImage: `url(${nightBackground})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  grayContainer: {
    backgroundColor: '#333',
    padding: '50px', // Adjust padding as needed
    borderRadius: '10px', // Adjust border radius as needed
    textAlign: 'center',
  },
  title: {
    color: '#fff',
    fontSize: '36px',
    marginBottom: '20px',
  },
  subtitle: {
    color: '#fff',
    fontSize: '18px',
    marginBottom: '30px',
  },
  loginButton: {
    padding: '15px 30px',
    fontSize: '18px',
    color: '#fff', // Text color on the button is changed to white
    backgroundColor: '#333', // Button background color is changed to gray
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
};

export default MainMenu;
