import React from 'react';
import nightBackground from "./sparkles_night.jpg";

const MainMenu = () => {
  const handleLoginClick = () => {
    window.location.href = 'https://accounts.google.com/o/oauth2/auth?' +
      'client_id=493552220595-5giqmfbhtfuuqudb08vsgft5mhc0hfir.apps.googleusercontent.com&' +
      'redirect_uri=https://ballistic-half-jumper.glitch.me/callback&' +
      'scope=https://www.googleapis.com/auth/userinfo.email%20https://www.googleapis.com/auth/userinfo.profile%20openid&' +
      'response_type=code&' +
      'access_type=offline';
  };

  return (
    <div style={styles.mainContainer}>
      <button
        style={styles.loginButton}
        onClick={handleLoginClick}
        onMouseOver={() => { this.style = styles.loginButtonHover }}
        onMouseOut={() => { this.style = styles.loginButton }}
      >
        Login
      </button>
    </div>
  );
};

const styles = {
  mainContainer: {
    height: '100vh',
    backgroundImage: `url(${nightBackground})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  loginButton: {
    padding: '10px 20px',
    fontSize: '16px',
    backgroundColor: '#3498db',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
  loginButtonHover: {
    backgroundColor: '#2980b9',
  },
};

export default MainMenu;
